function wpl_do_reset4(exclude, do_search) {
    if (!exclude) exclude = new Array();
    if (!do_search) do_search = false;

    wplj("#wpl_searchwidget_4").find(':input').each(function () {
        if (exclude.indexOf(this.id) != -1) return;

        switch (this.type) {
            case 'text':

                if(!wplj(this).parents().hasClass('chosen-choices'))
                {
                    wplj(this).val(null);
                }
                break;
                
            case 'select-multiple':

                wplj(this).find('option:selected').removeAttr('selected');
                wplj(this).trigger('chosen:updated');
                break;

            case 'select-one':

                wplj(this).val(wplj(this).find('option:first').val());
                wplj(this).trigger("chosen:updated");
                break;

            case 'password':
            case 'textarea':

                wplj(this).val('');
                break;

            case 'checkbox':
            case 'radio':
                
                wplj(this).removeAttr('checked');

                if(wplj(this).parent().find('input').val() == -1) wplj(this).nextAll('input[type="hidden"]').attr('value', '-1');
                else wplj(this).nextAll('input[type="hidden"]').attr('value', '');

                break;

            case 'hidden':

                elmid = this.id;
                idmin = elmid.indexOf("min");
                idmax = elmid.indexOf("max");
                idtmin = elmid.indexOf("tmin");
                idtmax = elmid.indexOf("tmax");

                if (idtmin != '-1') {
                    var table_column = elmid.split("_tmin_");
                    table_column = table_column[1];
                    var widget_id = elmid.split("_");
                    widget_id = parseInt(widget_id[0].replace("sf", ""));
                }
                else if (idtmax != '-1') {
                    var table_column = elmid.split("_tmax_");
                    table_column = table_column[1];
                    var widget_id = elmid.split("_");
                    widget_id = parseInt(widget_id[0].replace("sf", ""));
                }
                else if (idmin != '-1') {
                    var table_column = elmid.split("_min_");
                    table_column = table_column[1];
                    var widget_id = elmid.split("_");
                    widget_id = parseInt(widget_id[0].replace("sf", ""));
                }
                else if (idmax != '-1') {
                    var table_column = elmid.split("_max_");
                    table_column = table_column[1];
                    var widget_id = elmid.split("_");
                    widget_id = parseInt(widget_id[0].replace("sf", ""));
                }

                try {
                    var min_slider_value = wplj("#slider" + widget_id + "_range_" + table_column).slider("option", "min");
                    var max_slider_value = wplj("#slider" + widget_id + "_range_" + table_column).slider("option", "max");

                    wplj("#sf" + widget_id + "_tmin_" + table_column).val(min_slider_value);
                    wplj("#sf" + widget_id + "_tmax_" + table_column).val(max_slider_value);
                    wplj("#sf" + widget_id + "_min_" + table_column).val(min_slider_value);
                    wplj("#sf" + widget_id + "_max_" + table_column).val(max_slider_value);

                    wplj("#slider" + widget_id + "_range_" + table_column).slider("values", 0, min_slider_value);
                    wplj("#slider" + widget_id + "_range_" + table_column).slider("values", 1, max_slider_value);

                    wplj("#slider" + widget_id + "_showvalue_" + table_column).html(wpl_th_sep4(min_slider_value) + " - " + wpl_th_sep4(max_slider_value));
                }
                catch (err) {
                }
        }
    });

    if (do_search) wpl_do_search_4();
	
	}

function wpl_th_sep4(num) {
    sep = ",";
    num = num.toString();
    x = num;
    z = "";

    for (i = x.length - 1; i >= 0; i--)
        z += x.charAt(i);

    // add seperators. but undo the trailing one, if there
    z = z.replace(/(\d{3})/g, "$1" + sep);

    if (z.slice(-sep.length) == sep)
        z = z.slice(0, -sep.length);

    x = "";
    // reverse again to get back the number
    for (i = z.length - 1; i >= 0; i--)
        x += z.charAt(i);

    return x;
}


